import React, { useEffect } from "react";
import { useState } from "react";
import { Button, Box, TextField } from "@mui/material";
import DesoApi from "./libs/desoApi";
const Notification = () => {
  const [username, setUsername] = useState("");
  const [desoApi, setDesoApi] = useState(null);

  const [follower, setFollower] = useState([]);
  const [liker, setLiker] = useState([]);
  const [transfer, setTransfer] = useState([]);
  const [reposter, setReposter] = useState();
  const [commenter, setCommenter] = useState([]);
  const [loading, setLoading] = useState(true);

  const [usernames,setUsernames] = useState([]);
  const [usernamesFollowers, setUsernamesFollowers] = useState([]);
  const getNotifications = async () => {
    const PublicKeyBase58Check = username;
    const notificationsCall = await desoApi.getNotifications(
      PublicKeyBase58Check
    );
    setLiker(
      notificationsCall?.Notifications.map((x) =>
        x.Metadata.TxnType === "LIKE"
          ? [
              x.Metadata.TransactorPublicKeyBase58Check,
              x.Metadata.LikeTxindexMetadata.PostHashHex,
            ]
          : "x"
      ).filter((item, pos) => item.indexOf("x"))
    );
    setFollower(
      notificationsCall?.Notifications.map((x) =>
        x.Metadata.TxnType === "FOLLOW"
          ? [x.Metadata.TransactorPublicKeyBase58Check]
          : "x"
      ).filter((item, pos) => item.indexOf("x"))
    );
    setTransfer(
      notificationsCall?.Notifications.map((x) =>
        x.Metadata.TxnType === "BASIC_TRANSFER"
          ? [
              x.Metadata.TransactorPublicKeyBase58Check,
              x.Metadata.BasicTransferTxindexMetadata.DiamondLevel,
              x.Metadata.BasicTransferTxindexMetadata.PostHashHex,
            ]
          : "x"
      ).filter((item, pos) => item.indexOf("x"))
    );
  await  getUsers();
  };

  useEffect(() => {
    const da = new DesoApi();
    setDesoApi(da);
  }, []);
  useEffect(() => {
    if (liker || transfer || follower) {
      setLoading(false);
    } else {
      setLoading(true);
    }
  }, [liker, transfer, follower]);
  const getUsers = async()=>{
      const likers = liker.map(x=>x[0])
      const follower = follower.map(x=>x[0])
    const users = await desoApi.getUsersStateless(likers)
    const followerUsers = await desoApi.getUsersStateless(follower)
    console.log("USERRSS",users)
    setUsernames(users?.UserList.map(user=> user?.ProfileEntryResponse?.Username))
    setUsernamesFollowers(followerUsers?.UserList.map(user=> user?.ProfileEntryResponse?.Username))

  }
console.log("usernames",usernames)
console.log("usernames",usernamesFollowers)
  return (
    <div>
      <Box sx={{ mb: 2 }}>
        <TextField
          sx={{ width: "100%", mb: 2 }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          label="Get notifications of any profile using his/her public key"
        />
      </Box>
      <Box />
      <Button sx={{ mb: 2 }} variant="contained" onClick={getNotifications}>
        Get notifications
      </Button>
      {loading !== true ? (
        <>
          {/* <div>
            {follower.map((follow,index) => (
              <ul>
                <li> {usernames[index]} Followed you.</li>
              </ul>
            ))}
          </div> */}
          <div>
            {usernames.length>0 ? liker?.map((like,index) => (
              <ul>
                <li>
                  {" "}
                  {usernames[index]} Liked your post with the postHashHex {like[1]}.
                </li>
              </ul>
            )) : ""}
                 {usernamesFollowers.length>0 ? follower?.map((follow,index) => (
              <ul>
                <li>
                  {" "}
                  {usernames[index]} Followed you.
                </li>
              </ul>
            )) : ""}
          </div>
          {/* <div>
            {transfer.map((trans) => (
              <ul>
                <li> {trans[0]} Gave you {trans[1]} Diamond</li>
              </ul>
            ))}
          </div> */}
        </>
      ) : (
        "loading"
      )}
    </div>
  );
};

export default Notification;
